function register_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('register');
	if(!$arrPerm[1]){
		return false;
	}

	$data['client_name'] = makeSafe($_REQUEST['client_name']);
		if($data['client_name'] == empty_lookup_value){ $data['client_name'] = ''; }
	$data['seller'] = makeSafe($_REQUEST['seller']);
		if($data['seller'] == empty_lookup_value){ $data['seller'] = ''; }
	$data['date'] = parseCode('<%%creationDateTime%%>', true, true);
	$data['phone'] = makeSafe($_REQUEST['phone']);
		if($data['phone'] == empty_lookup_value){ $data['phone'] = ''; }
	$data['email'] = makeSafe($_REQUEST['email']);
		if($data['email'] == empty_lookup_value){ $data['email'] = ''; }
	if($data['client_name']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Sold to': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: register_before_insert
	if(function_exists('register_before_insert')){
		$args=array();
		if(!register_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `register` set       `client_name`=' . (($data['client_name'] !== '' && $data['client_name'] !== NULL) ? "'{$data['client_name']}'" : 'NULL') . ', `seller`=' . (($data['seller'] !== '' && $data['seller'] !== NULL) ? "'{$data['seller']}'" : 'NULL') . ', `date`=' . "'{$data['date']}'" . ', `phone`=' . (($data['phone'] !== '' && $data['phone'] !== NULL) ? "'{$data['phone']}'" : 'NULL') . ', `email`=' . (($data['email'] !== '' && $data['email'] !== NULL) ? "'{$data['email']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"register_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: register_after_insert
	if(function_exists('register_after_insert')){
		$res = sql("select * from `register` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!register_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='register', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

function register_delete($selected_id, $AllowDeleteOfParents=false, $skipChecks=false){
