function register_items_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('register_items');
	if(!$arrPerm[1]){
		return false;
	}

	$data['previous_order'] = makeSafe($_REQUEST['previous_order']);
		if($data['previous_order'] == empty_lookup_value){ $data['previous_order'] = ''; }
	$data['item_name'] = makeSafe($_REQUEST['item_name']);
		if($data['item_name'] == empty_lookup_value){ $data['item_name'] = ''; }
	$data['cost'] = makeSafe($_REQUEST['item_name']);
		if($data['cost'] == empty_lookup_value){ $data['cost'] = ''; }
	$data['ref_img'] = makeSafe($_REQUEST['item_name']);
		if($data['ref_img'] == empty_lookup_value){ $data['ref_img'] = ''; }
	if($data['cost'] == '') $data['cost'] = "0.00";

	// hook: register_items_before_insert
	if(function_exists('register_items_before_insert')){
		$args=array();
		if(!register_items_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `register_items` set       `item_name`=' . (($data['item_name'] !== '' && $data['item_name'] !== NULL) ? "'{$data['item_name']}'" : 'NULL') . ', `cost`=' . (($data['cost'] !== '' && $data['cost'] !== NULL) ? "'{$data['cost']}'" : 'NULL') . ', `ref_img`=' . (($data['ref_img'] !== '' && $data['ref_img'] !== NULL) ? "'{$data['ref_img']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"register_items_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// automatic previous_order
	if($_REQUEST['filterer_previous_order']){
		sql("update `register_items` set `previous_order`='" . makeSafe($_REQUEST['filterer_previous_order']) . "' where `id`='" . makeSafe($recID, false) . "'", $eo);
	}

	// hook: register_items_after_insert
	if(function_exists('register_items_after_insert')){
		$res = sql("select * from `register_items` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!register_items_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	sql("insert ignore into membership_userrecords set tableName='register_items', pkValue='" . makeSafe($recID, false) . "', memberID='" . makeSafe(getLoggedMemberID(), false) . "', dateAdded='" . time() . "', dateUpdated='" . time() . "', groupID='" . getLoggedGroupID() . "'", $eo);

	return $recID;
}

