	sql('update `register_items` set       `item_name`=' . (($data['item_name'] !== '' && $data['item_name'] !== NULL) ? "'{$data['item_name']}'" : 'NULL') . ', `cost`=' . (($data['cost'] !== '' && $data['cost'] !== NULL) ? "'{$data['cost']}'" : 'NULL') . ', `ref_img`=' . (($data['ref_img'] !== '' && $data['ref_img'] !== NULL) ? "'{$data['ref_img']}'" : 'NULL') . " where `id`='".makeSafe($selected_id)."'", $o);
	if($o['error']!=''){
		echo $o['error'];
		echo '<a href="register_items_view.php?SelectedID='.urlencode($selected_id)."\">{$Translation['< back']}</a>";
		exit;
	}


	// hook: register_items_after_update
	if(function_exists('register_items_after_update')){
