	// process combos
	$templateCode=str_replace('<%%COMBO(previous_order)%%>', $combo_previous_order->HTML, $templateCode);
	$templateCode=str_replace('<%%COMBOTEXT(previous_order)%%>', $combo_previous_order->MatchText, $templateCode);
	$templateCode=str_replace('<%%URLCOMBOTEXT(previous_order)%%>', urlencode($combo_previous_order->MatchText), $templateCode);
	$templateCode=str_replace('<%%COMBO(item_name)%%>', $combo_item_name->HTML, $templateCode);
	$templateCode=str_replace('<%%COMBOTEXT(item_name)%%>', $combo_item_name->MatchText, $templateCode);
	$templateCode=str_replace('<%%URLCOMBOTEXT(item_name)%%>', urlencode($combo_item_name->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array(  'previous_order' => array('register', 'Orders'), 'item_name' => array('inventory', 'Item name'));
	foreach($lookup_fields as $luf => $ptfc){
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']){
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']){
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode=str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(previous_order)%%>', '', $templateCode);
	$templateCode=str_replace('<%%UPLOADFILE(item_name)%%>', '', $templateCode);

	// process values
	if($selected_id){
		$templateCode=str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$templateCode=str_replace('<%%VALUE(previous_order)%%>', html_attr($row['previous_order']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(previous_order)%%>', urlencode($urow['previous_order']), $templateCode);
		$templateCode=str_replace('<%%VALUE(item_name)%%>', html_attr($row['item_name']), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(item_name)%%>', urlencode($urow['item_name']), $templateCode);
	}else{
		$templateCode=str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode=str_replace('<%%VALUE(previous_order)%%>', ( $_REQUEST['FilterField'][1]=='2' && $_REQUEST['FilterOperator'][1]=='<=>' ? $combo_previous_order->SelectedData : ''), $templateCode);
		$templateCode=str_replace('<%%URLVALUE(previous_order)%%>', urlencode( $_REQUEST['FilterField'][1]=='2' && $_REQUEST['FilterOperator'][1]=='<=>' ? $combo_previous_order->SelectedData : ''), $templateCode);
		$templateCode=str_replace('<%%VALUE(item_name)%%>', '', $templateCode);
		$templateCode=str_replace('<%%URLVALUE(item_name)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans){
		$templateCode=str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode=str_replace('<%%', '<!-- ', $templateCode);
	$templateCode=str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == ''){
		$templateCode .= "\n\n<script>\$j(function(){\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption){
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id){
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode.="\titem_name_update_autofills$rnd1 = function(){\n";
	$templateCode.="\t\tnew Ajax.Request(\n";
	if($dvprint){
		$templateCode.="\t\t\t'register_items_autofill.php?rnd1=$rnd1&mfk=item_name&id=' + encodeURIComponent('".addslashes($row['item_name'])."'),\n";
		$templateCode.="\t\t\t{encoding: 'UTF-8', method: 'get'}\n";
	}else{
		$templateCode.="\t\t\t'register_items_autofill.php?rnd1=$rnd1&mfk=item_name&id=' + encodeURIComponent(current_item_name{$rnd1}.value),\n";
		$templateCode.="\t\t\t{encoding: 'UTF-8', method: 'get', onCreate: function(){ \$j('#item_name$rnd1').prop('disabled', true); \$j('#item_nameLoading').html('<img src=loading.gif align=top>'); }, onComplete: function(){".(($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#item_name$rnd1').prop('disabled', false); " : "\$j('#item_name$rnd1').prop('disabled', true); ")."\$j('#item_nameLoading').html('');}}\n";
	}
	$templateCode.="\t\t);\n";
	$templateCode.="\t};\n";
	if(!$dvprint) $templateCode.="\tif(\$j('#item_name_caption').length) \$j('#item_name_caption').click(function(){ item_name_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	// hook: register_items_dv
	if(function_exists('register_items_dv')){
		$args=array();
		register_items_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>