		return false;
	}

	$data['repair_status'] = makeSafe($_REQUEST['repair_status']);
		if($data['repair_status'] == empty_lookup_value){ $data['repair_status'] = ''; }
	$data['item'] = makeSafe($_REQUEST['item']);
		if($data['item'] == empty_lookup_value){ $data['item'] = ''; }
